import React, { useState } from 'react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import './index.css';

const initialData = {
  columns: {
    todo: {
      name: 'Todo',
      items: [
        { id: '1', content: 'Design UI' },
        { id: '2', content: 'Setup project' },
        { id: '3', content: 'Write documentation' },
      ],
    },
    inprogress: {
      name: 'In Progress',
      items: [
        { id: '4', content: 'Develop features' },
      ],
    },
    done: {
      name: 'Done',
      items: [
        { id: '5', content: 'Project planning' },
      ],
    },
  },
};

function App() {
  const [data, setData] = useState(initialData);

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    // Same column
    if (source.droppableId === destination.droppableId) {
      const column = data.columns[source.droppableId];
      const copiedItems = Array.from(column.items);
      const [removed] = copiedItems.splice(source.index, 1);
      copiedItems.splice(destination.index, 0, removed);
      setData({
        columns: {
          ...data.columns,
          [source.droppableId]: {
            ...column,
            items: copiedItems,
          },
        },
      });
    } else {
      // Move between columns
      const sourceCol = data.columns[source.droppableId];
      const destCol = data.columns[destination.droppableId];
      const sourceItems = Array.from(sourceCol.items);
      const destItems = Array.from(destCol.items);
      const [removed] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
      setData({
        columns: {
          ...data.columns,
          [source.droppableId]: {
            ...sourceCol,
            items: sourceItems,
          },
          [destination.droppableId]: {
            ...destCol,
            items: destItems,
          },
        },
      });
    }
  };

  return (
    <div className="kanban-board">
      <DragDropContext onDragEnd={onDragEnd}>
        {Object.entries(data.columns).map(([colId, col]) => (
          <div className="kanban-column" key={colId}>
            <div className="kanban-column-title">{col.name}</div>
            <Droppable droppableId={colId}>
              {(provided, snapshot) => (
                <div
                  className="kanban-task-list"
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{ background: snapshot.isDraggingOver ? '#d2e4fc' : undefined }}
                >
                  {col.items.map((item, idx) => (
                    <Draggable key={item.id} draggableId={item.id} index={idx}>
                      {(provided, snapshot) => (
                        <div
                          className={`kanban-task${snapshot.isDragging ? ' dragging' : ''}`}
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                        >
                          {item.content}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </div>
        ))}
      </DragDropContext>
    </div>
  );
}

export default App;
